import React, { useState } from 'react';
import { Container, Row, Col, Button, ListGroup, Form, InputGroup } from 'react-bootstrap';

const MainInterface = () => {
    const [message, setMessage] = useState('');
    const [interviews, setInterviews] = useState(['Interview 1', 'Interview 2']);

    const handleNewInterview = () => {
        // 这里可以添加创建新面试的逻辑
        const newInterview = `Interview ${interviews.length + 1}`;
        setInterviews([...interviews, newInterview]);
    };

    const handleSendMessage = () => {
        // 这里可以添加发送信息的逻辑
        console.log(`Sent message: ${message}`);
        setMessage('');
    };

    return (
        <Container style={{ marginTop: '20px' }}>
            <Row>
                {/* 左边：历史面试 */}
                <Col xs={3}>
                    <Button variant="primary" onClick={handleNewInterview}>+ New Interview</Button>
                    <ListGroup variant="flush">
                        {interviews.map((interview, index) => (
                            <ListGroup.Item key={index}>{interview}</ListGroup.Item>
                        ))}
                    </ListGroup>
                    <Button variant="secondary">User Profile</Button>
                </Col>

                {/* 右边：创建新面试 */}
                <Col xs={9}>
                    <div style={{ minHeight: '400px', border: '1px solid #ccc', padding: '15px' }}>
                        {/* 这里可以放置信息栏 */}
                    </div>
                    <InputGroup className="mb-3" style={{ marginTop: '15px' }}>
                        <Form.Control
                            type="text"
                            placeholder="Type your message"
                            value={message}
                            onChange={(e) => setMessage(e.target.value)}
                        />
                        <InputGroup.Append>
                            <Button variant="outline-secondary" onClick={handleSendMessage}>Send</Button>
                        </InputGroup.Append>
                    </InputGroup>
                </Col>
            </Row>
        </Container>
    );
};

export default MainInterface;
