import React from 'react';
import { Container, Row, Col, ListGroup, Button, Form } from 'react-bootstrap';

const MainInterface = () => {
    return (
        <Container fluid>
            <Row>
                {/* 左侧：历史面试 */}
                <Col sm={4}>
                    <h2>历史面试</h2>
                    <ListGroup>
                        <ListGroup.Item>面试 1</ListGroup.Item>
                        <ListGroup.Item>面试 2</ListGroup.Item>
                        <ListGroup.Item>面试 3</ListGroup.Item>
                        {/* ... */}
                    </ListGroup>
                </Col>

                {/* 右侧：创建新面试 */}
                <Col sm={8}>
                    <h2>创建新面试</h2>

                    {/* 上传 CV */}
                    <Form.Group controlId="formCV">
                        <Form.Label>上传CV</Form.Label>
                        <Form.Control type="file" />
                    </Form.Group>

                    {/* 上传 Job Description */}
                    <Form.Group controlId="formJobDescription">
                        <Form.Label>上传Job Description</Form.Label>
                        <Form.Control type="file" />
                    </Form.Group>

                    {/* 选择面试官 */}
                    <Form.Group controlId="formInterviewer">
                        <Form.Label>选择面试官</Form.Label>
                        <Form.Control as="select">
                            <option>技术面试官</option>
                            <option>HR</option>
                            <option>老板</option>
                        </Form.Control>
                    </Form.Group>

                    {/* 开始面试按钮 */}
                    <Button variant="primary">开始对话式面试</Button>
                </Col>
            </Row>
        </Container>
    );
};

export default MainInterface;
